list_of_answers = ['Aboslutnie nie', 'Raczej nie', 'Raczej tak', 'Absolutnie tak']


class Character(object):
    total = 0
    personality = ''

    def __init__(self):
        self.total = 0
        self.personality = ''

    def calculate_answers(self, answers, type):
        self.total = 0
        for answer in answers:
            self.total += int(answer)
        if self.total <= 12:
            self.personality = self.personality + type[0]
            return self.personality
        elif self.total >= 13:
            self.personality = self.personality + type[1]
            return self.personality

    def __repr__(self):
        return f"{self.personality}"
